var Icon = React.createClass({
		render: function() {
			var useTag = '<use xlink:href="icons/svgIcons.svg#' + this.props.iconId + '" />';
			return React.createElement('svg', {
				className: 'icon',
				dangerouslySetInnerHTML: {__html: useTag }
			});
		}
	}
);

var Icons = React.createClass({
	render: function() {
		return React.createElement('ul', null, icons.map(function (iconName, index) {
			return React.createElement(Icon, { key: index, iconId: iconName });
		}));
	}
});

ReactDOM.render(
	React.createElement(Icons, { iconId: 'paper-stack' }),
	document.getElementById('root')
);
